-- find four characters that are all different,
-- keeping track of index

search count (a:b:c:d:rest) =
  if length (nub [a,b,c,d]) == 4
  then count + 4 -- since the 4th character is new
  else search (count + 1) (b:c:d:rest)


input = ""

-- find 14 characters that are all different,
-- keeping track of index
search' count line =
  let test = length (nub $ take 14 line) == 14
  in
    if test
    then count + 14
    else search' (count + 1) (drop 1 line)
